mapper.update:
UPDATE articles
SET views = views + 1
WHERE id = #{id}

UPDATE articles
SET status = #{status}, updated_at = #{updated_at}
WHERE id = #{id}

mapper.select:
SELECT
*
FROM articles a


        AND a.title LIKE CONCAT('%', #{title}, '%')


        AND a.category = #{category}


        AND a.created_at >= #{created_at}
